CREATE TABLE `attendance_log` (
  `id` INT AUTO_INCREMENT PRIMARY KEY,
  `name` VARCHAR(50),
  `class_date` DATE,
  `class_hours` FLOAT,
  `raw_hours` FLOAT,
  `attended_hours` FLOAT,
  `late_hours` FLOAT,
  `leave_early_hours` FLOAT,
  `absent_hours` FLOAT
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

